import axios from 'axios';

// Cambiar baseURL para asegurar consistencia
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api/v1';

export const apiClient = axios.create({
    baseURL: API_BASE_URL,
    headers: { 'Content-Type': 'application/json' },
});

apiClient.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');
        if (token) config.headers.Authorization = `Bearer ${token}`;
        return config;
    },
    (error) => Promise.reject(error)
);

apiClient.interceptors.response.use(
    (response) => response,
    (error) => {
        if (error.response?.status === 401) {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            window.location.href = '/login';
        }
        return Promise.reject(error);
    }
);

// Asegurar que todos los endpoints usen el prefijo /api/v1
export const authAPI = {
    login: (credentials) => apiClient.post('/auth/login', credentials),
    register: (userData) => apiClient.post('/auth/register', userData),
    logout: () => apiClient.post('/auth/logout'),
    getProfile: () => apiClient.get('/auth/profile'),
};

export const playersAPI = {
    getAll: (params = {}) => apiClient.get('/players', { params }),
    getById: (id) => apiClient.get(`/players/${id}`),
    create: (playerData) => apiClient.post('/players', playerData),
    update: (id, playerData) => apiClient.put(`/players/${id}`, playerData),
    delete: (id) => apiClient.delete(`/players/${id}`),
};

export const usersAPI = {
    getAll: (params = {}) => apiClient.get('/users', { params }),
    getById: (id) => apiClient.get(`/users/${id}`),
    update: (id, userData) => apiClient.put(`/users/${id}`, userData),
    delete: (id) => apiClient.delete(`/users/${id}`),
};

export const imagesAPI = {
    upload: (formData) => apiClient.post('/images/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
    }),
    getAll: (params = {}) => apiClient.get('/images', { params }),
    delete: (id) => apiClient.delete(`/images/${id}`),
};

export default apiClient;

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Components
import Header from './Header.jsx';
import Footer from './footer';
import Login from './Login';
import Signup from './Signup';
import Gallery from './Gallery';
import AdminDashboard from './AdminDashboard';
import PlayerList from './PlayerList';
import PlayerProfile from './PlayerProfile';
import Home from './Home';
import Profile from './Profile';

// Context
import { AuthProvider, useAuth } from '../context/AuthContext';

// Styles
import '../styles/App.css';

// Create a client
const queryClient = new QueryClient({
    defaultOptions: {
        queries: {
            refetchOnWindowFocus: false,
            retry: 1,
        },
    },
});


// Protected Route Component
const ProtectedRoute = ({ children, requireAdmin = false }) => {
    const { user, loading } = useAuth();

    if (loading) {
        return <div className="loading">Cargando...</div>;
    }

    if (!user) {
        return <Navigate to="/login" replace />;
    }

    if (requireAdmin && user.role !== 'admin') {
        return <Navigate to="/" replace />;
    }

    return children;
};

// Public Only Route Component
const PublicOnlyRoute = ({ children }) => {
    const { user, loading } = useAuth();

    if (loading) {
        return <div className="loading">Cargando...</div>;
    }

    if (user) {
        return <Navigate to="/" replace />;
    }

    return children;
};

// Main App Layout
const AppLayout = ({ children }) => {
    return (
        <div className="app">
            <Header />
            <main className="main-content">
                {children}
            </main>
            <Footer />
        </div>
    );
};

// Main App Component
function App() {
    return (
        <QueryClientProvider client={queryClient}>
            <AuthProvider>
                <Router>
                    <div className="App">
                        <Routes>
                            {/* Public Routes */}

                            <Route
                                path="/login"
                                element={
                                    <PublicOnlyRoute>
                                        <AppLayout>
                                            <Login />
                                        </AppLayout>
                                    </PublicOnlyRoute>
                                }
                            />
                            <Route
                                path="/signup"
                                element={
                                    <PublicOnlyRoute>
                                        <AppLayout>
                                            <Signup />
                                        </AppLayout>
                                    </PublicOnlyRoute>
                                }
                            />

                            {/* Public Routes */}
                            <Route
                                path="/"
                                element={
                                    <AppLayout>
                                        <Home />
                                    </AppLayout>
                                }
                            />
                            <Route
                                path="/gallery"
                                element={
                                    <AppLayout>
                                        <Gallery />
                                    </AppLayout>
                                }
                            />
                            <Route
                                path="/players"
                                element={
                                    <AppLayout>
                                        <PlayerList />
                                    </AppLayout>
                                }
                            />
                            <Route
                                path="/players/:id"
                                element={
                                    <AppLayout>
                                        <PlayerProfile />
                                    </AppLayout>
                                }
                            />

                            {/* Admin Routes */}
                            <Route
                                path="/admin"
                                element={
                                    <ProtectedRoute requireAdmin={true}>
                                        <AppLayout>
                                            <AdminDashboard />
                                        </AppLayout>
                                    </ProtectedRoute>
                                }
                            />
                            <Route
                                path="/profile"
                                element={
                                    <ProtectedRoute>
                                        <AppLayout>
                                            <Profile />
                                        </AppLayout>
                                    </ProtectedRoute>
                                }
                            />

                            {/* 404 Route */}
                            <Route
                                path="*"
                                element={
                                    <AppLayout>
                                        <div className="not-found">
                                            <h2>P√°gina no encontrada</h2>
                                            <p>La p√°gina que buscas no existe.</p>
                                        </div>
                                    </AppLayout>
                                }
                            />
                        </Routes>

                        {/* Toast notifications */}
                        <ToastContainer
                            position="top-right"
                            autoClose={5000}
                            hideProgressBar={false}
                            newestOnTop={false}
                            closeOnClick
                            rtl={false}
                            pauseOnFocusLoss
                            draggable
                            pauseOnHover
                        />
                    </div>
                </Router>
            </AuthProvider>
        </QueryClientProvider>
    );
}

export default App;

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import {
    Bars3Icon,
    XMarkIcon,
    UserIcon,
    ArrowRightOnRectangleIcon
} from '@heroicons/react/24/outline';

const Header = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const { user, logout, isAuthenticated, isAdmin, authVersion } = useAuth();
    // Log para depuraci√≥n de contexto
    console.log('HEADER CONTEXT:', { user, logout, isAuthenticated, isAdmin, authVersion });
    const navigate = useNavigate();

    const handleLogout = async () => {
        await logout();
        navigate('/login');
        setIsMenuOpen(false);
    };

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const closeMenu = () => {
        setIsMenuOpen(false);
    };

    return (
        <header className="header">
            <div className="header-container">
                {/* Logo */}
                <div className="header-logo">
                    <Link to="/" onClick={closeMenu}>
                        <h1>üèÄ Basketball Team</h1>
                    </Link>
                </div>

                {/* Desktop Navigation */}
                <nav className="desktop-nav">
                    <ul className="nav-links">
                        <li>
                            <Link to="/">Inicio</Link>
                        </li>
                        <li>
                            <Link to="/gallery">Galer√≠a</Link>
                        </li>
                        <li>
                            <Link to="/players">Jugadores</Link>
                        </li>
                        {isAdmin && (
                            <li>
                                <Link to="/admin">Admin</Link>
                            </li>
                        )}
                    </ul>

                    {/* User Menu simplificado */}
                    <div className="user-menu">
                        {isAuthenticated ? (
                            <button className="login-btn" onClick={handleLogout}>
                                Cerrar Sesi√≥n
                            </button>
                        ) : (
                            <Link to="/login" className="login-btn">
                                Iniciar Sesi√≥n
                            </Link>
                        )}
                    </div>
                </nav>

                {/* Mobile Menu Button */}
                <button
                    className="mobile-menu-btn"
                    onClick={toggleMenu}
                    aria-label="Toggle menu"
                >
                    {isMenuOpen ? (
                        <XMarkIcon className="h-6 w-6" />
                    ) : (
                        <Bars3Icon className="h-6 w-6" />
                    )}
                </button>
            </div>

            {/* Mobile Navigation */}
            {isMenuOpen && (
                <div className="mobile-nav">
                    <div className="mobile-nav-content">
                        <ul className="mobile-nav-links">
                            <li>
                                <Link to="/" onClick={closeMenu}>Inicio</Link>
                            </li>
                            <li>
                                <Link to="/gallery" onClick={closeMenu}>Galer√≠a</Link>
                            </li>
                            <li>
                                <Link to="/players" onClick={closeMenu}>Jugadores</Link>
                            </li>
                            {isAdmin && (
                                <li>
                                    <Link to="/admin" onClick={closeMenu}>Admin</Link>
                                </li>
                            )}
                        </ul>

                        {/* Mobile User Menu */}
                        <div className="mobile-user-menu">
                            {isAuthenticated ? (
                                <>
                                    <div className="mobile-user-info">
                                        <UserIcon className="h-5 w-5" />
                                        <span>{user?.email}</span>
                                    </div>
                                    {/* <Link to="/profile" onClick={closeMenu} className="mobile-menu-item">Mi Perfil</Link> */}
                                    <button
                                        onClick={handleLogout}
                                        className="mobile-menu-item logout-btn"
                                    >
                                        <ArrowRightOnRectangleIcon className="h-4 w-4" />
                                        Cerrar Sesi√≥n
                                    </button>
                                </>
                            ) : (
                                <div className="mobile-auth-buttons">
                                    <Link to="/login" onClick={closeMenu} className="mobile-login-btn">
                                        Iniciar Sesi√≥n
                                    </Link>
                                    <Link to="/signup" onClick={closeMenu} className="mobile-signup-btn">
                                        Registrarse
                                    </Link>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            )}
        </header>
    );
};

export default Header;

import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const { login, loading } = useAuth();
    const navigate = useNavigate();
    const [form, setForm] = useState({ email: '', password: '' });
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');

    // LOG: render Login
    console.log('[LOGIN] render', form);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const validate = () => {
        if (!form.email || !form.password) {
            setError('Todos los campos son obligatorios');
            return false;
        }
        // Validaci√≥n b√°sica de email
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(form.email)) {
            setError('Email no v√°lido');
            return false;
        }
        setError('');
        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validate()) return;
        setError('');
        // LOG: submit login
        console.log('[LOGIN] submit', form);
        const result = await login(form);
        // LOG: resultado login
        console.log('[LOGIN] result', result);
        if (result.success) {
            navigate('/');
        } else {
            setError(result.error || 'Error al iniciar sesi√≥n');
        }
    };

    return (
        <div className="login-container">
            <form className="login-form" onSubmit={handleSubmit} autoComplete="off">
                <h2>Iniciar Sesi√≥n</h2>
                <label>
                    Email
                    <input
                        type="email"
                        name="email"
                        value={form.email}
                        onChange={handleChange}
                        required
                        autoComplete="username"
                    />
                </label>
                <label>
                    Contrase√±a
                    <div className="password-input-wrapper">
                        <input
                            type={showPassword ? 'text' : 'password'}
                            name="password"
                            value={form.password}
                            onChange={handleChange}
                            required
                            autoComplete="current-password"
                        />
                        <button
                            type="button"
                            className="show-password-btn"
                            onClick={() => setShowPassword((v) => !v)}
                            tabIndex={-1}
                            aria-label={showPassword ? 'Ocultar contrase√±a' : 'Mostrar contrase√±a'}
                        >
                            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                        </button>
                    </div>
                </label>
                {error && <div className="form-error">{error}</div>}
                <button
                    type="submit"
                    className="btn-primary"
                    disabled={loading}
                >
                    {loading ? 'Ingresando...' : 'Ingresar'}
                </button>
            </form>
        </div>
    );
};

export default Login;

import React from 'react';
import { useQuery } from 'react-query';
import { useNavigate } from 'react-router-dom';
import { playersAPI } from '../api/client';

const PlayerList = () => {
    const navigate = useNavigate();
    const {
        data: playersData,
        isLoading,
        error,
        refetch
    } = useQuery('players', () => playersAPI.getAll());

    // Funci√≥n para navegar al perfil del jugador
    const handlePlayerClick = (playerId) => {
        navigate(`/players/${playerId}`);
    };

    if (isLoading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Cargando jugadores...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <h3>Error al cargar jugadores</h3>
                <p>{error.response?.data?.msg || 'Error desconocido'}</p>
                <button onClick={refetch} className="btn-primary">
                    Reintentar
                </button>
            </div>
        );
    }

    const players = playersData?.data?.data || [];

    return (
        <div className="player-list">
            <div className="player-list-header">
                <h2>Nuestros Jugadores</h2>
                <p>Conoce a los integrantes de nuestro equipo</p>
                <div className="list-stats">
                    <div className="stat-chip">
                        <span className="stat-number">{players.length}</span>
                        <span className="stat-text">Jugadores</span>
                    </div>
                    {playersData?.data?.meta?.total && (
                        <div className="stat-chip">
                            <span className="stat-number">{playersData.data.meta.total}</span>
                            <span className="stat-text">Total</span>
                        </div>
                    )}
                </div>
            </div>

            {players.length === 0 ? (
                <div className="empty-state">
                    <h3>No hay jugadores registrados</h3>
                    <p>S√© el primero en unirte al equipo</p>
                </div>
            ) : (
                <div className="players-grid">
                    {players.map((player) => (
                        <div
                            key={player._id}
                            className="player-card clickable"
                            onClick={() => handlePlayerClick(player._id)}
                            title={`Ver perfil de ${player.name}`}
                        >
                            <div className="player-avatar">
                                <img
                                    src={player.avatar || '/default-avatar.svg'}
                                    alt={`${player.name || 'Jugador'}`}
                                />
                            </div>

                            <div className="player-info">
                                <h3>{player.name || 'Nombre no disponible'}</h3>
                                <p className="player-position">{player.position}</p>

                                <div className="player-stats">
                                    <div className="stat">
                                        <span className="stat-label">Altura:</span>
                                        <span className="stat-value">{player.height} cm</span>
                                    </div>
                                    <div className="stat">
                                        <span className="stat-label">Peso:</span>
                                        <span className="stat-value">{player.weight} kg</span>
                                    </div>
                                </div>

                                {player.stats && (
                                    <div className="player-performance">
                                        <h4>Estad√≠sticas</h4>
                                        <div className="performance-stats">
                                            <div className="perf-stat">
                                                <span>Partidos:</span>
                                                <span>{player.stats.games_played || 0}</span>
                                            </div>
                                            <div className="perf-stat">
                                                <span>Puntos/Partido:</span>
                                                <span>{player.stats.points_per_game || 0}</span>
                                            </div>
                                            <div className="perf-stat">
                                                <span>Rebotes/Partido:</span>
                                                <span>{player.stats.rebounds_per_game || 0}</span>
                                            </div>
                                            <div className="perf-stat">
                                                <span>Asistencias/Partido:</span>
                                                <span>{player.stats.assists_per_game || 0}</span>
                                            </div>
                                        </div>
                                    </div>
                                )}

                                {/* Bot√≥n para ver m√°s detalles */}
                                <div className="player-actions">
                                    <button
                                        className="btn btn-primary view-profile-btn"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            handlePlayerClick(player._id);
                                        }}
                                    >
                                        Ver Perfil Completo
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}

            {/* Pagination */}
            {playersData?.data?.meta?.total > playersData?.data?.meta?.limit && (
                <div className="pagination">
                    {/* Add pagination controls here */}
                    <p>
                        Mostrando {players.length} de {playersData.data.meta.total} jugadores
                    </p>
                </div>
            )}
        </div>
    );
};

export default PlayerList;

import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery } from 'react-query';
import { playersAPI } from '../api/client';
import '../styles/PlayerProfile.css';

const PlayerProfile = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    // Query para obtener los datos del jugador
    const {
        data: playerData,
        isLoading,
        isError,
        error,
        refetch
    } = useQuery(
        ['player', id],
        () => playersAPI.getById(id),
        {
            enabled: !!id,
            retry: 2,
            onError: (error) => {
                console.error('Error fetching player:', error);
            }
        }
    );

    // Estados de carga y error
    if (isLoading) {
        return (
            <div className="player-profile">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Cargando informaci√≥n del jugador...</p>
                </div>
            </div>
        );
    }

    if (isError) {
        return (
            <div className="player-profile">
                <div className="error-container">
                    <h2>Error al cargar jugador</h2>
                    <p>{error?.response?.data?.message || 'No se pudo cargar la informaci√≥n del jugador'}</p>
                    <div className="error-actions">
                        <button onClick={() => refetch()} className="btn btn-primary">
                            Intentar de nuevo
                        </button>
                        <button onClick={() => navigate('/players')} className="btn btn-secondary">
                            Volver a la lista
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    const player = playerData?.data?.data;

    if (!player) {
        return (
            <div className="player-profile">
                <div className="error-container">
                    <h2>Jugador no encontrado</h2>
                    <p>El jugador que buscas no existe o ha sido eliminado.</p>
                    <button onClick={() => navigate('/players')} className="btn btn-primary">
                        Volver a la lista
                    </button>
                </div>
            </div>
        );
    }

    // Funci√≥n para formatear fecha
    const formatDate = (dateString) => {
        if (!dateString) return 'No especificado';
        return new Date(dateString).toLocaleDateString('es-ES', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    // Funci√≥n para formatear altura
    const formatHeight = (height) => {
        if (!height) return 'No especificado';
        return `${height} cm`;
    };

    // Funci√≥n para formatear peso
    const formatWeight = (weight) => {
        if (!weight) return 'No especificado';
        return `${weight} kg`;
    };

    return (
        <div className="player-profile">
            {/* Breadcrumbs */}
            <nav className="breadcrumbs">
                <span onClick={() => navigate('/')} className="breadcrumb-item">
                    üè† Inicio
                </span>
                <span className="breadcrumb-separator">‚Ä∫</span>
                <span onClick={() => navigate('/players')} className="breadcrumb-item">
                    üë• Jugadores
                </span>
                <span className="breadcrumb-separator">‚Ä∫</span>
                <span className="breadcrumb-current">
                    {player ? `üë§ ${player.name}` : 'Cargando...'}
                </span>
            </nav>

            {/* Header con navegaci√≥n */}
            <div className="profile-header">
                <button
                    onClick={() => navigate('/players')}
                    className="btn btn-back"
                >
                    ‚Üê Volver a la lista
                </button>
                <h1>Perfil del Jugador</h1>
                <div className="header-actions">
                    <button
                        onClick={() => refetch()}
                        className="btn btn-refresh"
                        title="Actualizar informaci√≥n"
                    >
                        üîÑ Actualizar
                    </button>
                </div>
            </div>            {/* Informaci√≥n principal del jugador */}
            <div className="profile-content">
                <div className="profile-main">
                    <div className="player-card">
                        <div className="player-avatar">
                            <img
                                src={player.photo || '/default-avatar.svg'}
                                alt={player.name}
                                onError={(e) => {
                                    e.target.src = '/default-avatar.svg';
                                }}
                            />
                        </div>
                        <div className="player-info">
                            <h2>{player.name}</h2>
                            <p className="player-position">{player.position}</p>
                            {player.user_id && (
                                <div className="user-info">
                                    <span className="user-email">
                                        üìß {player.user_id.email}
                                    </span>
                                    <span className={`user-status ${player.user_id.isActive ? 'active' : 'inactive'}`}>
                                        {player.user_id.isActive ? 'üü¢ Activo' : 'üî¥ Inactivo'}
                                    </span>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Informaci√≥n f√≠sica */}
                    <div className="info-section">
                        <h3>Informaci√≥n F√≠sica</h3>
                        <div className="info-grid">
                            <div className="info-item">
                                <label>Altura:</label>
                                <span>{formatHeight(player.height)}</span>
                            </div>
                            <div className="info-item">
                                <label>Peso:</label>
                                <span>{formatWeight(player.weight)}</span>
                            </div>
                            <div className="info-item">
                                <label>Fecha de nacimiento:</label>
                                <span>{formatDate(player.birthdate)}</span>
                            </div>
                            <div className="info-item">
                                <label>N√∫mero de camiseta:</label>
                                <span>{player.jersey_number || 'No asignado'}</span>
                            </div>
                        </div>
                    </div>

                    {/* Estad√≠sticas */}
                    {player.stats && (
                        <div className="info-section">
                            <h3>Estad√≠sticas por Partido</h3>
                            <div className="stats-grid">
                                <div className="stat-item">
                                    <span className="stat-value">{player.stats.points_per_game || 0}</span>
                                    <span className="stat-label">Puntos</span>
                                </div>
                                <div className="stat-item">
